function retval = acorsum (y)
  asum = 1:1:length(y);
  i = 1.0;
  while i <= length(y)
    k = 1.0;
    s = 0;
    while k <= (length(y) - i)
      s = s + (y(k)-128)*(y(k+i)-128)/256;
      k = k + 1;
    endwhile;
    asum(i) = s;
    
    i = i + 1;
  endwhile;
 
  retval = asum;
endfunction

